import styled from 'styled-components'

import Card from 'components/Card'
import { AutoColumn } from 'components/Column'

export const PageWrapper = styled.div`
  padding: 28px 16px 100px;
  width: 100%;

  @media only screen and (min-width: 768px) {
    padding: 24px 16px 100px;
  }

  @media only screen and (min-width: 1000px) {
    padding: 24px 32px 100px;
  }

  @media only screen and (min-width: 1366px) {
    padding: 24px 215px 50px;
  }

  @media only screen and (min-width: 1440px) {
    padding: 24px 252px 50px;
  }
`

export const Container = styled.div`
  max-width: 936px;
  margin: 0 auto;
  padding: 4px 20px 24px;
  background: ${({ theme }) => theme.background};
  box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.01), 0px 4px 8px rgba(0, 0, 0, 0.04), 0px 16px 24px rgba(0, 0, 0, 0.04),
    0px 24px 32px rgba(0, 0, 0, 0.01);
  border-radius: 20px;

  @media only screen and (min-width: 1000px) {
    padding: 4px 24px 24px;
  }
`

export const GridColumn = styled.div`
  display: grid;
  grid-template-columns: 1fr;

  @media only screen and (min-width: 768px) {
    grid-template-columns: 1fr 1fr;
  }
`

export const TopBar = styled.div`
  padding: 20px 0;
  display: flex;
  flex-direction: column-reverse;
  align-items: center;
  border-top: 1px solid ${({ theme }) => theme.border};
  margin-top: 1rem;

  @media only screen and (min-width: 768px) {
    display: grid;
    grid-template-columns: 1fr 1fr;
    margin-top: 0;
  }
`

export const LiquidityProviderModeWrapper = styled.div`
  width: 100%;

  @media only screen and (min-width: 768px) {
    padding-right: 24px;
  }
`

export const PoolName = styled.div`
  display: flex;
  margin-bottom: 24px;
  font-size: 14px;
  font-weight: 500;
  color: ${({ theme }) => theme.subText};

  @media only screen and (min-width: 768px) {
    justify-content: flex-end;
    margin-bottom: 0;
  }
`

export const FirstColumn = styled(AutoColumn)`
  grid-auto-rows: min-content;
  padding-bottom: 24px;
  border-bottom: 1px solid ${({ theme }) => theme.border};
  gap: 1rem;

  @media only screen and (min-width: 768px) {
    padding-right: 24px;
    padding-bottom: 0;
    border-right: 1px solid ${({ theme }) => theme.border};
    border-bottom: none;
  }
`

export const SecondColumn = styled(AutoColumn)`
  grid-auto-rows: min-content;
  padding-top: 24px;

  @media only screen and (min-width: 768px) {
    padding-left: 24px;
    padding-top: 0;
  }
`

export const TokenWrapper = styled.div`
  display: flex;
  align-items: center;
  gap: 4px;
`

export const ActiveText = styled.div`
  font-weight: 500;
  font-size: 12px;
  color: ${({ theme }) => theme.subText};
`

export const Section = styled(Card)`
  padding: 16px 12px;
  border: 1px solid ${({ theme }) => theme.border};

  @media only screen and (min-width: 768px) {
    padding: 16px;
  }
`

export const CurrentPriceWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  width: 100%;
  border-bottom: 1px dashed ${({ theme }) => theme.border};
  padding-bottom: 8px;
`

export const PoolRatioWrapper = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  width: 100%;
`

export const DynamicFeeRangeWrapper = styled(AutoColumn)`
  flex: 1;
  justify-content: flex-start;
`

export const USDPrice = styled.div`
  display: flex;
  align-items: center;
  font-size: 12px;
  font-weight: 500;
  font-stretch: normal;
  font-style: normal;
  line-height: normal;
  letter-spacing: normal;
  padding-left: 8px;
  color: ${({ theme }) => theme.subText};
`

export const Warning = styled.div`
  display: flex;
  background: ${({ theme }) => `${theme.warning}20`};
  border-radius: 999px;
  padding: 0.75rem 1rem;
  margin-bottom: 24px;
`

export const DetailWrapper = styled(AutoColumn)`
  padding: 1rem 1rem 12px;
  border: 1px solid ${({ theme }) => theme.border};
  margin: 24px 0 28px;
  border-radius: 4px;
`

export const DetailBox = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
`
