import { configureStore } from '@reduxjs/toolkit';
import { load, save } from 'redux-localstorage-simple';
import application from './application/reducer';
import burnProAmm from './burn/proamm/reducer';
import burn from './burn/reducer';
import campaigns from './campaigns/reducer';
import customizeDexes from './customizeDexes';
import prommFarms from './farms/promm/reducer';
import prommNfts from './nfts/promm/reducer';
import farms from './farms/reducer';
import { updateVersion } from './global/actions';
import lists from './lists/reducer';
import mintV2 from './mint/proamm/reducer';
import mint from './mint/reducer';
import multicall from './multicall/reducer';
import pair from './pair/reducer';
import pools from './pools/reducer';
import swap from './swap/reducer';
import transactions from './transactions/reducer';
import tutorial from './tutorial/reducer';
import user from './user/reducer';
import vesting from './vesting/reducer';
var PERSISTED_KEYS = ['user', 'transactions', 'lists'];
var store = configureStore({
  devTools: process.env.NODE_ENV !== 'production',
  reducer: {
    application: application,
    user: user,
    transactions: transactions,
    swap: swap,
    mint: mint,
    mintV2: mintV2,
    burn: burn,
    burnProAmm: burnProAmm,
    multicall: multicall,
    lists: lists,
    pair: pair,
    pools: pools,
    farms: farms,
    prommFarms: prommFarms,
    prommNfts: prommNfts,
    vesting: vesting,
    // [dataApi.reducerPath]: dataApi.reducer
    campaigns: campaigns,
    tutorial: tutorial,
    customizeDexes: customizeDexes
  },
  middleware: function middleware(getDefaultMiddleware) {
    return getDefaultMiddleware({
      thunk: false,
      immutableCheck: false,
      serializableCheck: false
    }) // .concat(dataApi.middleware)
    .concat(save({
      states: PERSISTED_KEYS,
      debounce: 1000
    }));
  },
  preloadedState: load({
    states: PERSISTED_KEYS
  })
});
store.dispatch(updateVersion()); // setupListeners(store.dispatch)

export default store;
