const ErrorDetail = styled(Section)`
  padding: 12px;
  border-radius: 4px;
  margin-top: 12px;
  color: ${({ theme }) => theme.text};
  background-color: ${({ theme }) => `${theme.buttonBlack}66`};
  font-size: 10px;
  width: 100%;
  text-align: center;
  line-height: 16px;
`

const StyledAlert = styled(Alert)`
  height: 108px;
  width: 108px;
`
export function TransactionErrorContent({ message, onDismiss }: { message: string; onDismiss: () => void }) {
  const theme = useTheme()
  const [showDetail, setShowDetail] = useState<boolean>(false)
  return (
    <Wrapper>
      <Section>
        <RowBetween>
          <Text fontWeight={500} fontSize={20}>
            <Trans>Error</Trans>
          </Text>
          <CloseIcon onClick={onDismiss} />
        </RowBetween>
        <AutoColumn style={{ marginTop: 20 }} gap="8px" justify="center">
          <StyledAlert />
          <Text
            fontWeight={500}
            fontSize={16}
            color={theme.red}
            lineHeight={'24px'}
            style={{ textAlign: 'center', width: '85%' }}
          >
            {errorFriendly(message)}
            {/* {message.includes('minTotalAmountOut') &&
              ' Try to refresh the exchange rate or increase the Slippage tolerance in Settings'} */}
          </Text>
          {message !== errorFriendly(message) && (
            <AutoColumn justify="center" style={{ width: '100%' }}>
              <Text
                color={theme.primary}
                fontSize="14px"
                sx={{ cursor: `pointer` }}
                onClick={() => setShowDetail(!showDetail)}
              >
                Show more details
              </Text>
              {showDetail && <ErrorDetail>{message}</ErrorDetail>}
            </AutoColumn>
          )}
        </AutoColumn>
      </Section>
      <BottomSection gap="12px">
        <ButtonPrimary onClick={onDismiss}>
          <Trans>Dismiss</Trans>
        </ButtonPrimary>
      </BottomSection>
    </Wrapper>
  )
}

interface ConfirmationModalProps {
  isOpen: boolean
  onDismiss: () => void
  hash: string | undefined
  content: () => React.ReactNode
  attemptingTxn: boolean
  pendingText: string | React.ReactNode
  tokenAddToMetaMask?: Currency
  showTxBanner?: boolean
}

export default function TransactionConfirmationModal({
  isOpen,
  onDismiss,
  attemptingTxn,
  hash,
  pendingText,
  content,
  tokenAddToMetaMask,
  showTxBanner,
}: ConfirmationModalProps) {
  const { chainId } = useActiveWeb3React()

  if (!chainId) return null

  return (
    <Modal isOpen={isOpen} onDismiss={onDismiss} maxHeight={90}>
      {attemptingTxn ? (
        <ConfirmationPendingContent onDismiss={onDismiss} pendingText={pendingText} />
      ) : hash ? (
        <TransactionSubmittedContent
          showTxBanner={showTxBanner}
          chainId={chainId}
          hash={hash}
          onDismiss={onDismiss}
          tokenAddToMetaMask={tokenAddToMetaMask as Token}
        />
      ) : (
        content()
      )}
    </Modal>
  )
}
