interface AccountDetailsProps {
  toggleWalletModal: () => void
  pendingTransactions: string[]
  confirmedTransactions: string[]
  ENSName?: string
  openOptions: () => void
}

export default function AccountDetails({
  toggleWalletModal,
  pendingTransactions,
  confirmedTransactions,
  ENSName,
  openOptions,
}: AccountDetailsProps) {
  const { chainId, account, connector, deactivate } = useWeb3React()
  const theme = useTheme()
  const dispatch = useDispatch<AppDispatch>()

  function formatConnectorName() {
    const { ethereum } = window
    const isInjected = !!(ethereum && ethereum.isMetaMask)
    const isCoin98 = isInjected && ethereum.isCoin98
    const isBraveWallet = isInjected && ethereum.isBraveWallet

    const walletConfig = (() => {
      if (isInjected) {
        if (isCoin98) {
          return SUPPORTED_WALLETS['COIN98']
        }

        if (isBraveWallet) {
          return SUPPORTED_WALLETS['BRAVE']
        }

        return SUPPORTED_WALLETS['METAMASK']
      }

      return Object.values(SUPPORTED_WALLETS).find(config => config.connector === connector)
    })()

    const name = walletConfig?.name || ''

    if (!walletConfig) {
      console.error('Cannot find the wallet connect')
    }

    return (
      <WalletName>
        <Trans>Connected with {name}</Trans>
      </WalletName>
    )
  }

  function getStatusIcon() {
    switch (connector) {
      case injected:
      case coin98InjectedConnector:
      case braveInjectedConnector: {
        return (
          <IconWrapper size={20}>
            <Identicon />
          </IconWrapper>
        )
      }

      case walletconnect: {
        return (
          <IconWrapper size={20}>
            <img src={WalletConnectIcon} alt={'wallet connect logo'} />
          </IconWrapper>
        )
      }

      case walletlink: {
        return (
          <IconWrapper size={20}>
            <img src={CoinbaseWalletIcon} alt={'coinbase wallet logo'} />
          </IconWrapper>
        )
      }

      case fortmatic: {
        return (
          <IconWrapper size={20}>
            <img src={FortmaticIcon} alt={'fortmatic logo'} />
          </IconWrapper>
        )
      }

      case portis: {
        return (
          <IconWrapper size={20}>
            <img src={PortisIcon} alt={'portis logo'} />
            <MainWalletAction
              onClick={() => {
                portis.portis.showPortis()
              }}
            >
              <Trans>Show Portis</Trans>
            </MainWalletAction>
          </IconWrapper>
        )
      }

      default: {
        return null
      }
    }
  }

  const clearAllTransactionsCallback = useCallback(() => {
    if (chainId) dispatch(clearAllTransactions({ chainId }))
  }, [dispatch, chainId])

  const [, setIsUserManuallyDisconnect] = useLocalStorage('user-manually-disconnect')

  const handleDisconnect = () => {
    deactivate()

    // @ts-expect-error close can be returned by wallet
    if (connector && connector.close) connector.close()
    setIsUserManuallyDisconnect(true)
  }

  return (
    <>
      <UpperSection>
        <HeaderRow>
          <Trans>Account</Trans>
          <CloseIcon onClick={toggleWalletModal}>
            <CloseColor />
          </CloseIcon>
        </HeaderRow>

        <Flex flexDirection="column" marginTop="8px" paddingX="20px">
          {formatConnectorName()}

          <YourAccount>
            <AccountGroupingRow id="web3-account-identifier-row">
              <AccountControl>
                {ENSName ? (
                  <div>
                    {getStatusIcon()}
                    <p> {ENSName}</p>
                  </div>
                ) : (
                  <div>
                    {getStatusIcon()}
                    <p> {isMobile && account ? shortenAddress(account, 10) : account}</p>
                  </div>
                )}
              </AccountControl>
            </AccountGroupingRow>

            <CopyHelper toCopy={account || ''} />
          </YourAccount>
        </Flex>

        <Flex justifyContent="space-between" marginTop="24px" paddingX="20px">
          <ExternalLink href={getEtherscanLink(chainId || ChainId.MAINNET, ENSName || account || '', 'address')}>
            <Flex alignItems="center">
              <FileText size={16} />
              <Text marginLeft="4px" fontSize="14px">
                <Trans>View Transactions</Trans> ↗
              </Text>
            </Flex>
          </ExternalLink>

          <ExternalLink href={`${PROMM_ANALYTICS_URL[chainId as ChainId]}/account/${account}`}>
            <Flex alignItems="center">
              <Wallet size={16} />
              <Text fontSize="14px" marginLeft="4px">
                <Trans>Analyze Wallet</Trans> ↗
              </Text>
            </Flex>
          </ExternalLink>
        </Flex>

        <Flex justifyContent="space-between" marginTop="24px" paddingX="20px" sx={{ gap: '1rem' }}>
          <ButtonOutlined onClick={handleDisconnect}>
            <Trans>Disconnect</Trans>
          </ButtonOutlined>
          <ButtonPrimary
            onClick={() => {
              openOptions()
            }}
          >
            <Trans>Change Wallet</Trans>
          </ButtonPrimary>
        </Flex>
      </UpperSection>

      <Flex marginTop="24px" paddingX="20px" width="100%">
        <Divider style={{ width: '100%' }} />
      </Flex>
      {!!pendingTransactions.length || !!confirmedTransactions.length ? (
        <LowerSection>
          <AutoRow mb={'1rem'} style={{ justifyContent: 'space-between' }}>
            <TYPE.body>
              <Trans>Recent Transactions</Trans>
            </TYPE.body>
            <LinkStyledButton onClick={clearAllTransactionsCallback}>(clear all)</LinkStyledButton>
          </AutoRow>
          {renderTransactions(pendingTransactions.slice(0, 5))}
          {renderTransactions(confirmedTransactions.slice(0, 5))}
        </LowerSection>
      ) : (
        <LowerSection>
          <TYPE.body color={theme.text}>
            <Trans>Your transactions will appear here...</Trans>
          </TYPE.body>
        </LowerSection>
      )}
    </>
  )
}
