import { DialogContent, DialogOverlay } from '@reach/dialog'
import '@reach/dialog/styles.css'
import { transparentize } from 'polished'
import React from 'react'
import { isMobile } from 'react-device-detect'
import { animated, useSpring, useTransition } from 'react-spring'
import { useGesture } from 'react-use-gesture'
import styled, { css } from 'styled-components'

const AnimatedDialogOverlay = animated(DialogOverlay)

const StyledDialogOverlay = styled(AnimatedDialogOverlay)<{ zindex: string | number }>`
  &[data-reach-dialog-overlay] {
    z-index: ${({ zindex }) => zindex};
    overflow: hidden;

    display: flex;
    align-items: center;
    justify-content: center;

    background-color: ${({ theme }) => theme.modalBG};
  }
`

const AnimatedDialogContent = animated(DialogContent)
// destructure to not pass custom props to Dialog DOM element
const StyledDialogContent = styled(
  ({ borderRadius, minHeight, maxHeight, maxWidth, width, height, bgColor, mobile, isOpen, margin, ...rest }) => (
    <AnimatedDialogContent {...rest} />
  ),
).attrs({
  'aria-label': 'dialog',
})`
  overflow-y: ${({ mobile }) => (mobile ? 'scroll' : 'hidden')};

  &[data-reach-dialog-content] {
    margin: ${({ margin }) => margin || '0 0 2rem 0'};
    background-color: ${({ theme, bgColor }) => bgColor || theme.tableHeader};
    box-shadow: 0 4px 8px 0 ${({ theme }) => transparentize(0.95, theme.shadow1)};
    padding: 0;
    width: ${({ width }) => width || '50vw'};
    height: ${({ height }) => height || 'auto'};
    overflow-y: ${({ mobile }) => (mobile ? 'scroll' : 'hidden')};
    overflow-x: hidden;
    align-self: ${({ mobile }) => (mobile ? 'flex-end' : 'center')};
    max-width: ${({ maxWidth }) => (maxWidth && !isNaN(maxWidth) ? `${maxWidth}px` : maxWidth)};
    ${({ maxHeight }) =>
      maxHeight &&
      `
        max-height: ${maxHeight && !isNaN(maxHeight) ? `${maxHeight}vh` : maxHeight};
      `}
    ${({ minHeight }) =>
      minHeight &&
      `
        min-height: ${minHeight}vh;
      `}
    display: flex;
    ${({ borderRadius }) =>
      borderRadius &&
      `
        border-radius: ${borderRadius};
      `}
    ${({ theme, width }) => theme.mediaWidth.upToMedium`
      width:  ${width || '65vw'};
      margin: 0;
    `}
    ${({ theme, mobile, borderRadius }) => theme.mediaWidth.upToSmall`
      width:  85vw;
      ${
        mobile &&
        `
          width: 100vw;
          border-radius: ${borderRadius};
          border-bottom-left-radius: 0;
          border-bottom-right-radius: 0;
        `
      }
    `}
  }
`

export interface ModalProps {
  isOpen: boolean
  onDismiss: () => void
  minHeight?: number | false
  maxHeight?: number | string
  maxWidth?: number | string
  borderRadius?: number | string
  width?: string
  height?: string
  bgColor?: string
  zindex?: number | string
  margin?: string
  enableInitialFocusInput?: boolean
  className?: string
  children?: React.ReactNode
  transition?: boolean
}

export default function Modal({
  isOpen,
  onDismiss,
  minHeight = false,
  margin = '',
  maxHeight = 90,
  maxWidth = 420,
  width,
  height,
  bgColor,
  enableInitialFocusInput = false,
  className,
  children,
  transition = true,
  zindex = 100,
  borderRadius = '20px',
}: ModalProps) {
  const fadeTransition = useTransition(isOpen, {
    config: { duration: transition ? 200 : 0 },
    from: { opacity: 0 },
    enter: { opacity: 1 },
    leave: { opacity: 0 },
  })

  const [{ y }, set] = useSpring(() => ({ y: 0, config: { mass: 1, tension: 210, friction: 20 } }))
  const bind = useGesture({
    onDrag: state => {
      set({
        y: state.down ? state.movement[1] : 0,
      })
      if (state.movement[1] > 300 || (state.velocity > 3 && state.direction[1] > 0)) {
        onDismiss()
      }
    },
  })
  return (
    <>
      {fadeTransition(
        (style, item) =>
          item && (
            <StyledDialogOverlay zindex={zindex} style={style} onDismiss={onDismiss}>
              <StyledDialogContent
                {...(isMobile
                  ? {
                      ...bind(),
                      style: { transform: y.interpolate(y => `translateY(${(y as number) > 0 ? y : 0}px)`) },
                    }
                  : {})}
                aria-label="dialog content"
                minHeight={minHeight}
                maxHeight={maxHeight}
                maxWidth={maxWidth}
                margin={margin}
                width={width}
                height={height}
                bgColor={bgColor}
                borderRadius={borderRadius}
                mobile={isMobile}
                className={className}
              >
                {/* prevents the automatic focusing of inputs on mobile by the reach dialog */}
                {!enableInitialFocusInput && isMobile ? <div tabIndex={1} /> : null}
                {children}
              </StyledDialogContent>
            </StyledDialogOverlay>
          ),
      )}
    </>
  )
}

export const ModalCenter = styled(Modal)`
  ${isMobile &&
  css`
    align-self: unset !important;
    border-radius: 24px !important;
  `}
`
